import { useContext, useState } from "react";
import { AuthContext } from "./AuthContext";
import { Redirect } from "react-router";
import jwt_decode from "jwt-decode";

export const UploadVideo = (props) => {
  const [file, setFile] = useState("");
  const [token] = useContext(AuthContext);
  const [filePicked, setFilePicked] = useState(false);
  const decodedToken = jwt_decode(token);
  const { userId, userRol } = decodedToken;
  const [errorMsg, setErrorMsg] = useState(null);
  const videoIdUser = userId;

  function uploadFile() {
    let data = new FormData();
    data.append("profileVideo", file);
    fetch(`http://localhost:8000/api/v1/videos/user/${videoIdUser}`, {
      method: "POST",
      body: data,
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Access-Control-Allow-Methods":
          "GET, POST, PUT, DELETE, PATCH, OPTIONS",
        "Access-Control-Allow-Headers":
          "X-Requested-With, content-type, Authorization",
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then((success) => "Archivo subido correctamente")
      .catch((error) => console.log(error));
  }
  const onFileChange = (event) => {
    setFile(event.target.files[0]);
    setFilePicked(true);
  };

  return (
    <>
      {token && userRol === "Player" ? (
        <div className="App">
          <form onSubmit={uploadFile}>
            <div>
              <label>Subir Vídeo</label>
              <input type="file" onChange={onFileChange} />
            </div>
            <button type="submit">Subir Vídeo</button>
          </form>
        </div>
      ) : (
        <Redirect to="/profile" />
      )}
    </>
  );
};
