import moment from "moment";

export const getAge = (birthday) => {
  let birth = moment(birthday, "YYYY-MM-DD").format("YYYY-MM-DD");
  let today = moment();
  let age = 0;
  if (birth < today) {
    age = moment().diff(birth, "years");
  } else {
    console.error(
      "La fecha de nacimiento no puede ser superior a la fecha actual del sistema."
    );
  }
  return age;
};
