import { useState, useContext } from "react";
import { useLocation } from "react-router-dom";
import { AuthContext } from "./AuthContext";
import axios from "axios";

export const CreateContact = () => {
  const [token] = useContext(AuthContext);
  const [response, setResponse] = useState("");
  const [contactDescription, setContactDescription] = useState("");
  let data = useLocation();

  const request = async () => {
    const newcontact = { contactDescription: contactDescription };
    const respuesta = await fetch(
      `http://localhost:8000/api/v1/contact/user/${data.state.id}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newcontact),
      }
    );
    if (respuesta.ok) {
      const resMessage = await respuesta.json();
      setResponse(resMessage);
    }
  };
  return (
    <div>
      <h1>Contacta con el Jugador</h1>
      <p></p>
      <form id={"requestPopUp"} onSubmit={request}>
        <label htmlFor="contactDescription">
          Descripci√≥n
          <textarea
            id="contactDescription"
            value={contactDescription}
            onChange={(e) => setContactDescription(e.target.value)}
          />
        </label>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};
