import { useState, useContext } from "react";
import { useLocation } from "react-router-dom";
import { AuthContext } from "./AuthContext";
import axios from "axios";

export const CreateContact = () => {
  const [token] = useContext(AuthContext);
  const [contactDescription, setContactDescription] = useState("");
  let data = useLocation();
  const userId = data.state.id;

  const request = async () => {
    try {
      const respuesta = await axios.post(
        `http://localhost:8000/api/v1/contact/user/${userId}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(contactDescription),
        }
      );
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div>
      <h1>Contacta con el Jugador</h1>
      <p></p>
      <form id={"requestPopUp"} onSubmit={request}>
        <label htmlFor="contactDescription">
          Descripci√≥n
          <textarea
            id="contactDescription"
            value={contactDescription}
            onChange={(e) => setContactDescription(e.target.value)}
          />
        </label>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};
