import React, { useContext, useEffect } from "react";
import { useState } from "react";
import { AuthContext } from "./AuthContext";
import jwt_decode from "jwt-decode";
import useUserProfile from "../hooks/useUserProfile";
import ProfileImage from "./ProfileImage";
import { UploadProfileImage } from "./UploadProfileImage";
import { getAge } from "../utils/getAge";
import moment from "moment";
import { categories } from "../utils/categories";
import { useParams } from "react-router-dom";
import { GetSkills } from "./Skills";

function GetProfileUser() {
  const [token] = useContext(AuthContext);
  const decodedToken = jwt_decode(token);
   let { userId } = useParams();
   let isSelfProfile = false;

  const [formState, setFormState] = useState("");

  if (!userId) {
    userId = decodedToken.userId;
  }

 const [userInfo] = useUserProfile(userId);

  const birth = moment(userInfo.userInfo.userBirthday).format("YYYY-MM-DD");
  const age = getAge(birth);
  const category = categories(age);
  return (
    <>
      {userInfo.userInfo.userRol === "Player" ? <div>{userInfo.userInfo.userNumber}</div> : ""}
      <div>{userInfo.userInfo.userName}</div>
      <div>
        {userInfo.userInfo.userImage ? (
          <img
            src={`/images/profiles/${userInfo.userInfo.userImage}`}
            alt="Imagen de perfil"
            className="profileimage"
          ></img>
        ) : (
          <img
            src={`/images/profiles/image-default.png`}
            alt="Imagen de perfil"
            className="profileimage"
          ></img>
        )}
      </div>
      {userInfo.userRol === "Player" ? (
        <div>
          Edad: {age} años <span>({category})</span>
        </div>
      ) : (
        ""
      )}
      {userInfo.userRol === "Player" ? (
        <div>Posición: {userInfo.userPosition} </div>
      ) : (
        ""
      )}
      <div>Equipo: {userInfo.userTeam} </div>
      <div>Localidad: {userInfo.userLocation} </div>
      <div>
        <p>{userInfo.userDescription}</p>
      </div>
      <GetSkills />
    </>
  );
}

export { GetProfileUser };
