import React, { useContext, useEffect } from "react";
import { useState } from "react";

export function MainPage() {
  const [user, setUser] = useState([]);
  const [userVideo, setUserVideo] = useState([]);
  const [userImage, setUserImage] = useState("");
  const [videoIduser, setVideoIduser] = useState("");
  const [userInfoReloader, setUserInfoReloader] = useState(0);
  const refreshUserInfo = () => setUserInfoReloader(Math.random());

  useEffect(() => {
    const loadVideos = async () => {
      const response = await fetch(`http://localhost:8000/api/v1/videos/`);
      if (response.ok) {
        const body2 = await response.json();
        setUserVideo(body2);
        setVideoIduser(body2.videoIduser);
      }
    };
    loadVideos();
  }, []);

  useEffect(() => {
    const loadUserInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/users/profiles/`
      );
      if (response.ok) {
        const body = await response.json();
        setUser(body.user);
      }
    };
    loadUserInfo();
  }, []);

  let videoUrl = userVideo
    .map((i) => {
      return { videoUrl: i.videoUrl, videoIduser: i.videoIduser };
    })
    .reverse();

  let datosuser = user.map((i) => {
    return { userName: i.userName, userImage: i.userImage, userId: i.userId };
  });

  const userData = datosuser.filter(
    (i) => (i.userId = videoUrl.find((x) => (x.videoIduser = i.userId)))
  );

  // const userData2 = userData.map((z) => {
  //   return {
  //     userName: z.userName,
  //     videoUrl: z.userId.videoUrl,
  //     userImage: z.userImage,
  //     userData
  //   };
  // });

  console.log(datosuser);

  return (
    <div>
      <ul style={{ listStyle: "none", paddingLeft: 0 }}>
        {videoUrl.map((url, index, ar) => {
          return (
            <li key={url.videoUrl}>
              <video
                controls
                src={`/videos/${url.videoUrl}`}
                type="video/mp4"
                width="320"
                height="180"
              >
                {" "}
              </video>
              <p>
                {userData
                  .filter((x) => (x.userId = ar.videoIduser))
                  .map((i) => i.userName)}
              </p>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
