import { useState,useEffect,useContext } from "react";
import React from "react";
import { AuthContext } from "./AuthContext";
import jwt_decode from "jwt-decode";
import { FaStar } from "react-icons/fa";


function GetSkills() {
  const [userInfo, setUserInfo] = useState([]);
  const [userPuntuation, setUserPuntuation] = useState(3);
  const [token, setToken] = useContext(AuthContext);
  const decodedToken = jwt_decode(token);
  const { userId, userRol } = decodedToken;
  const [formState, setFormState] = useState("");
  const [hover, setHover] = useState(null);

  const [userInfoReloader, setUserInfoReloader] = useState(0);
  const refreshUserInfo = () => setUserInfoReloader(Math.random());

  useEffect(() => {
    const loadSkillInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/users/skills/${userId}`
      );
      if (response.ok) {
        const body = await response.json();
        setUserInfo(body);
      }
    };
    loadSkillInfo();
  }, [userId]);

  useEffect(()=>{
    const loadPuntuationInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/ratings/user/${userId}`
      );
      if(response.ok){
        const body2 = await response.json();
        setUserPuntuation(body2);
      } 
    };
    loadPuntuationInfo();
  }, [userId]);

  let ratingValue =userPuntuation.map((x)=>{
                     const p= x.find(userInfo.skillName);
                      return p.userPuntuation
                    });

  return (
    <div>
      <ul style={{ listStyle: "none", paddingLeft: 0 }}>
        {userInfo.map((item) => (
          <li key={item.positionSkillId}>
            {item.skillName}{" "}
            {[...Array(5)].map((star, i) => {
              return (
                <label>
                  <input
                    type="radio"
                    name="rating"
                    value={ratingValue}
                    onClick={() => setUserPuntuation(ratingValue)}
                  ></input>
                  <FaStar
                    className="star"
                    size={100}
                    color={
                      ratingValue <= (hover || userPuntuation) ? "#ffc107" : "#e4e5e9"
                    }
                    onMouseEnter={() => setHover(ratingValue)}
                    onMouseLeave={() => setHover(null)}
                  />
                </label>
              );
            })}
          </li>
        ))}
      </ul>
    </div>
  );
}

export { GetSkills };
