import React, { useRef, useContext, useEffect, useState } from "react";
import { AuthContext } from "./AuthContext";
import { Link } from "react-router-dom";
import jwt_decode from "jwt-decode";
import { useDetectOutsideClick } from "./../hooks/useDetectedOutsideClick";

const Menu = () => {
  const dropdownRef = useRef(null);
  const [isActive, setIsActive] = useDetectOutsideClick(dropdownRef, false);
  const onClick = () => setIsActive(!isActive);
  const [userInfo, setUserInfo] = useState("");
  const [token, setToken] = useContext(AuthContext);
  const decodedToken = jwt_decode(token);
  const { userId } = decodedToken;
  const [userImage, setUserImage] = useState("");
  const [formState, setFormState] = useState("");

  useEffect(() => {
    const loadUserInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/users/profiles/${userId}`
      );
      if (response.ok) {
        const body = await response.json();
        setUserImage(body.user.userImage);
        setFormState("activo");
      }
    };
    loadUserInfo();
  }, [formState, userId]);

  return !token ? (
    <div className="container-menu">
      <div className="menu-container">
        <button onClick={onClick} className="menu-trigger">
          <img src={`/images/profiles/image-default.png`} alt="User avatar" />
        </button>
        <nav
          ref={dropdownRef}
          className={`menu ${isActive ? "active" : "inactive"}`}
        >
          <ul>
            <li>
              <Link to="/login">Iniciar sesión</Link>
            </li>
            <li>
              <Link to="/register">Regístrate</Link>
            </li>
          </ul>
        </nav>
      </div>
    </div>
  ) : (
    <div className="container-menu">
      <div className="menu-container">
        <button onClick={onClick} className="menu-trigger">
          <img src={`/images/profiles/image-default.png`} alt="User avatar" />
        </button>
        <nav
          ref={dropdownRef}
          className={`menu ${isActive ? "active" : "inactive"}`}
        >
          <ul>
            <li>
              <Link to="/profile/user-profile">Tu perfil</Link>
            </li>
            <li>
              <Link to="/logout">Logout</Link>
            </li>
          </ul>
        </nav>
      </div>
    </div>
  );
};

export default Menu;
