import { useState,useEffect,useContext } from "react";
import React from "react";
import { AuthContext } from "./AuthContext";
import jwt_decode from "jwt-decode";
import StarRating from "./StarRating";


function GetSkills() {
  const [userInfo, setUserInfo] = useState([]);
  const [userPuntuation, setUserPuntuation] = useState();
  const [token, setToken] = useContext(AuthContext);
  const decodedToken = jwt_decode(token);
  const { userId, userRol } = decodedToken;
  const [formState, setFormState] = useState("");

  const [userInfoReloader, setUserInfoReloader] = useState(0);
  const refreshUserInfo = () => setUserInfoReloader(Math.random());

  useEffect(() => {
    const loadSkillInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/users/skills/${userId}`
      );
      if (response.ok) {
        const body = await response.json();
        setUserInfo(body);
      }
    };
    loadSkillInfo();
  }, [userId]);

  useEffect(()=>{
    const loadPuntuationInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/ratings/user/${userId}`
      );
      if(response.ok){
        const body2 = await response.json();
        setUserPuntuation(body2);
      } 
    };
    loadPuntuationInfo();
  }, [userId]);

  return (
    <div>
      <ul style={{ listStyle: "none", paddingLeft: 0 }}>
        {userInfo.map((item) => (
          <li key={item.positionSkillId}>{item.skillName}</li>
        ))}
      </ul>
    </div>
  );
}

export { GetSkills };
