import { useEffect, useContext } from "react";
import { AuthContext } from "./AuthContext";
import { Link } from "react-router-dom";
import ImageDefault from "../imagesMenu/image-default.png";
import jwt_decode from "jwt-decode";
import Menucheck from "../utils/Menucheck";
import LogOut from "./Logout";

const Menu = () => {
  const [token] = useContext(AuthContext);

  const currentPage = window.location.pathname;

  useEffect(() => {
    Menucheck();
  }, []);

  const toggleMenu = (e) => {
    e.preventDefault();
    const menu = document.getElementById("menu");
    menu.classList.toggle("hidden");
  };

  return (
    <aside id="menu" className="hidden">
      <button onClick={toggleMenu}>
        <img src={ImageDefault} alt="Ojeador App" onClick={toggleMenu} />
        {token === " " ? (
          <ul>
            {currentPage !== "/login" && (
              <li>
                <Link to="/login">Iniciar sesión</Link>
              </li>
            )}
            {currentPage !== "/register" && (
              <li>
                <Link to="/register">Regístrate</Link>
              </li>
            )}
          </ul>
        ) : (
          <ul>
            {currentPage !== "/login" && (
              <li>
                <Link to="/profile/user-profile">Perfil</Link>
              </li>
            )}
            {currentPage !== "/logout" && <li>Logout</li>}
          </ul>
        )}
      </button>
    </aside>
  );
};

export default Menu;
