import React, { useContext, useEffect } from "react";
import { useState } from "react";
import { AuthContext } from "./AuthContext";
import jwt_decode from "jwt-decode";
import useUserProfile from "../hooks/useUserProfile";
import ProfileImage from "./ProfileImage";
import { UploadProfileImage } from "./UploadProfileImage";
import { getAge } from "../utils/getAge";
import moment from "moment";
import { categories } from "../utils/categories";
import { useParams } from "react-router-dom";
import { GetSkills } from "./Skills";

function ScoutProfile() {
  const [token] = useContext(AuthContext);
  const decodedToken = jwt_decode(token);
  let { userId } = useParams();


  if (!userId) {
    userId = decodedToken.userId;
  }

  const [userInfo] = useUserProfile(userId);

  return (
    <>
      <div>{userInfo.user.userName}</div>
      <div>
        {userInfo.user.userImage ? (
          <img
            src={`/images/profiles/${userInfo.user.userImage}`}
            alt="Imagen de perfil"
            className="profileimage"
          ></img>
        ) : (
          <img
            src={`/images/profiles/image-default.png`}
            alt="Imagen de perfil"
            className="profileimage"
          ></img>
        )}
      </div>
      <div>Equipo: {userInfo.user.userTeam} </div>
      <div>Localidad: {userInfo.user.userLocation} </div>
      <div>
        <p>{userInfo.user.userDescription}</p>
      </div>
    </>
  );
}

export { ScoutProfile };
