import { useState, useContext } from "react";
import { useLocation } from "react-router-dom";
import { AuthContext } from "./AuthContext";

export const CreateContact = () => {
  let [id, setId] = useState("");
  const [token] = useContext(AuthContext);
  const [contactDescription, setContactDescription] = useState("");
  let data = useLocation();

  let userId = (data) => {
    if (!data) {
      return " ";
    } else {
      return data.state.id;
    }
  };

  id = userId();

  console.log(id);
  const request = async (e) => {
    console.log("content", contactDescription);
    await fetch(`http://localhost:8000/api/v1/contact/user/${id}/contact/`, {
      method: "POST",
      headers: {
        "Content-type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ contactDescription }),
    });
  };

  return (
    <div>
      <h1>Contacta con el Jugador</h1>
      <p></p>
      <form id={"requestPopUp"} onSubmit={request}>
        <textarea
          name="reqContent"
          id="reqContent"
          value={contactDescription}
          onChange={(e) => setContactDescription(e.target.value)}
          placeholder={"Escribe aquí..."}
        />
        <button>Enviar</button>
      </form>
    </div>
  );
};
