import { AuthContext } from "./AuthContext";
import jwt_decode from "jwt-decode";

export const UploadProfileImage = () => {
  const [file, setFile] = useState();
  const [filePicked, setFilePicked] = useState(false);
  const [token, setToken] = useContext(AuthContext);
    const decodedToken = jwt_decode(token);
    const { userId, user } = decodedToken;

  async function uploadFile() {
    let data = new FormData();
    console.log(data);
    data.append("profileImage", file);
   const response = await fetch(`http://localhost:8000/api/v1/users/profile/upload-image/${userId}`, {
      method: "POST",
      body: data,
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })

    if(response.ok){setFilePicked
      const body = await response.json();

    }
  }
  const onFileChange = (event) => {
    setFile(event.target.files[0]);
    setFilePicked(true);
  };

  return (
    <div className="App">
      <form className="upload-image-container" onSubmit={uploadFile}>
        <div className="upload-image-label">
          <label id="titulo-avatar">Editar foto de perfil</label>
          <div className="input-personalizado">
            <label className="texto">Seleccionar archivo</label>
            <input className="file" type="file" onChange={onFileChange} />
          </div>
        </div>
        <button id="boton" type="submit">
          Actualizar Foto de Perfil
        </button>
      </form>
    </div>
  );
};
