import React, { useContext, useEffect } from "react";
import { useState } from "react";
import { AuthContext } from "./AuthContext";
import jwt_decode from "jwt-decode";
import ProfileImage from "./ProfileImage";
import { UploadProfileImage } from "./UploadProfileImage";

function UpdatePlayer() {
  const [userName, setUserName] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const [userLocation, setUserLocation] = useState("");
  const [userTeam, setUserTeam] = useState("");
  const [userNumber, setUserNumber] = useState("");
  const [userBirthday, setUserBirthday] = useState("");
  const [userPosition, setUserPosition] = useState("Portero");
  const [userDescription, setUserDescription] = useState("");
  const [response, setResponse] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [token, setToken] = useContext(AuthContext);
  const decodedToken = jwt_decode(token);
  const { userId } = decodedToken;
  const [formState, setFormState] = useState("");

  const [userInfoReloader, setUserInfoReloader] = useState(1);
  const refreshUserInfo = () => setUserInfoReloader(Math.random());

  console.log(decodedToken);
  useEffect(() => {
    const loadUserInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/users/profiles/${userId}`
      );
      if (response.status === 200) {
        const body = await response.json();
        setUserInfoReloader(body);
        setUserEmail(body.userEmail);
        setUserName(body.userName);
        setUserTeam(body.userTeam);
        setUserNumber(body.userNumber);
        setUserDescription(body.userDescription);
        setUserLoc
        setFormState("activo");
      }
    };
    loadUserInfo();
  }, [formState, userId]);

  async function handleSubmit(event) {
    event.preventDefault();

    const nuevoUsuarioServidor = {
      userName: userName,
      userEmail: userEmail,
      userBirthday: userBirthday,
      userDescription: userDescription,
      userLocation: userLocation,
      userNumber: userNumber,
      userPosition: userPosition,
      userTeam: userTeam,
    };

    const res = await fetch(`http://localhost:8000/api/v1/users/profiles/update/${userId}`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(nuevoUsuarioServidor),
    });
    if (res.status === 200) {
      const resMessage = await res.json();
      console.log(resMessage);
      setResponse("Usuario actualizado");
    } else {
      const resMessage = await res.json();

      setResponse(resMessage.error);
      console.log(errorMsg);
    }
    refreshUserInfo();
  }
  return (
    <div className="updateuser-container">
      <div className="update-user-wrapper">
        <p className="update-user-wrapper-p">EDITAR USUARIO</p>
        {/* <ProfileImage id="avatar-profile" userInfoReloader={userInfoReloader} />
        <div className="upload-image-wrapper">
          <div className="upload-image">
            <UploadProfileImage />
          </div>
        </div> */}

        <div className="update-user">
          <form onSubmit={handleSubmit}>
            <label>
              <p>Nombre</p>
              <input
                className="update-user-input"
                type="text"
                name="userName"
                value={userName}
                onChange={(e) => setUserName(e.target.value)}
              />
            </label>
            <label>
              <p>Email</p>
              <input
                className="update-user-input"
                type="email"
                name="userEmail"
                value={userEmail}
                onChange={(e) => setUserEmail(e.target.value)}
              />
            </label>
            <label>
              <p>Localidad</p>
              <input
                className="update-user-input"
                type="text"
                name="userLocation"
                value={userLocation}
                onChange={(e) => setUserLocation(e.target.value)}
              />
            </label>
            <label>
              <p>Equipo</p>
              <input
                className="update-user-input"
                type="text"
                name="userTeam"
                value={userTeam}
                onChange={(e) => setUserTeam(e.target.value)}
              />
            </label>
            <label>
              <p>Número camiseta</p>
              <input
                className="update-user-input"
                type="number"
                name="userNumber"
                value={userNumber}
                onChange={(e) => setUserNumber(e.target.value)}
              />
            </label>
            <label>
              <p>Fecha de Nacimiento</p>
              <input
                className="update-user-input"
                type="date"
                name="userBirthday"
                value={userBirthday}
                onChange={(e) => setUserBirthday(e.target.value)}
              />
            </label>

            <p>Elija su posición</p>
            <select
              onChange={(e) => setUserPosition(e.target.value)}
              value={userPosition}
            >
              <option value="Portero" key="Portero">
                Portero
              </option>
              <option value="Defensa" key="Defensa">
                Defensa
              </option>
              <option value="Mediocentro" key="Mediocentro">
                Mediocentro
              </option>
              <option value="Delantero" key="Delantero">
                Delantero
              </option>
            </select>
            <label>
              Descripción
              <textarea
                value={userDescription}
                onChange={(e) => setUserDescription(e.target.value)}
              />
            </label>
            <div className="update-user-button">
              <button type="submit">Guardar cambios</button>
            </div>
          </form>
          <div className="response-message-player">
            {response && <div>{response}</div>}
          </div>
        </div>
      </div>
    </div>
  );
}

export { UpdatePlayer };
