import { useContext, useState } from "react";
import { AuthContext } from "./AuthContext";
import { Redirect } from "react-router";

export const UploadProfileImage = (props) => {
  const [file, setFile] = useState("");
  const [token] = useContext(AuthContext);
  const [errorMsg, setErrorMsg] = useState(null);

  async function uploadFile() {
    let data = new FormData();
    data.append("image", file);
    const image = await fetch(
      "http://localhost:8000/api/v1/users/profiles/upload-photo/${user",
      {
        method: "POST",
        body: data,
      }
    );
    const response = await image.json();
    if (response.ok) {
      setErrorMsg(null);
    } else {
      setErrorMsg(`Error`);
    }
  }
  const onFileChange = (event) => {
    const f = event.target.files[0];
    setFile(f);
  };
  return (
    <>
      {token ? (
        <div className="App">
          <form onSubmit={uploadFile}>
            <div>
              <label>Selecciona Foto de Perfil</label>
              <input type="file" onChange={onFileChange} />
            </div>
            <button type="submit">Cambiar Foto</button>
          </form>
        </div>
      ) : (
        <Redirect to="/profile" />
      )}
    </>
  );
};
