import React, { useContext, useEffect } from "react";
import { useState } from "react";

export function MainPage() {
  const [userName, setUserName] = useState("");
  const [response, setResponse] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [userVideo, setUserVideo] = useState([]);
  const [videoUrl, setVideoUrl] = useState("");
  const [userImage, setUserImage] = useState("");
  const [videoIduser, setVideoIduser] = useState("");
  let id = videoIduser;
  const [formState, setFormState] = useState("");

  const [userInfoReloader, setUserInfoReloader] = useState(0);
  const refreshUserInfo = () => setUserInfoReloader(Math.random());

  useEffect(() => {
    const loadUserInfo = async () => {
      const response = await fetch(
        `http://localhost:8000/api/v1/users/profiles/${id}`
      );
      if (response.ok) {
        const body = await response.json();
        setUserName(body.user.userName);
        setUserImage(body.user.userImage);
      }
    };
    loadUserInfo();
  }, [id]);

  useEffect(() => {
    const loadVideos = async () => {
      const response = await fetch(`http://localhost:8000/api/v1/videos/`);
      if (response.ok) {
        const body2 = await response.json();
        setUserVideo(body2.body.video);
        setVideoIduser(body2.body.video.videoIduser);
        setVideoUrl(body2.body.video.videoUrl);
      }
    };
    loadVideos();
  }, []);
  console.log(videoUrl);
  return (
    <div>
      <ul style={{ listStyle: "none", paddingLeft: 0 }}>
        {userVideo.map((video, index) => {
          return (
            <li key={video.videoUrl}>
              <video controls src={`/videos/${videoUrl}`} type="video/mp4">
                {" "}
              </video>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
